import styled from 'styled-components';

export const Container = styled.div`
    margin-top: 2rem;
    table {
        width: 100%;
        border-spacing: 0 1rem;
        th {
            color: var(--text-table);
            font-weight: bold;
            padding: 1rem 2rem;
            text-align: left;
            line-height: 1.5rem;
        }   
        td {
            padding: 1rem 2rem;
            border: 0;
            background: var(--shape);
            color: var(--text-body);
            &:first-child {
                color: var(--text-title);
            }
        }
    }    
`;
